import React, { useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  Alert,
  LinearProgress,
} from '@mui/material';
import {
  Save as SaveIcon,
  CloudUpload as CloudUploadIcon,
  Description as DescriptionIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { toast } from 'react-toastify';
import { useDropzone } from 'react-dropzone';

import { expedienteService } from '../services/expedienteService';

const schema = yup.object({
  nombre_solicitante: yup
    .string()
    .required('El nombre del solicitante es requerido')
    .min(2, 'El nombre debe tener al menos 2 caracteres')
    .max(255, 'El nombre no puede exceder 255 caracteres'),
  dni: yup
    .string()
    .required('El DNI es requerido')
    .min(8, 'El DNI debe tener al menos 8 caracteres')
    .max(20, 'El DNI no puede exceder 20 caracteres'),
  area: yup
    .string()
    .required('El área es requerida')
    .min(2, 'El área debe tener al menos 2 caracteres')
    .max(100, 'El área no puede exceder 100 caracteres'),
  descripcion: yup
    .string()
    .max(1000, 'La descripción no puede exceder 1000 caracteres'),
  numero_expediente: yup
    .string()
    .min(4, 'El número de expediente debe tener al menos 4 caracteres')
    .max(50, 'El número de expediente no puede exceder 50 caracteres'),
});

const CreateExpedientePage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onDrop = (acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      const file = acceptedFiles[0];
      setUploadedFile(file);
      toast.success(`Archivo "${file.name}" seleccionado correctamente`);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'image/*': ['.png', '.jpg', '.jpeg', '.gif'],
    },
    maxFiles: 1,
    maxSize: 10485760, // 10MB
    onDropRejected: (rejectedFiles) => {
      const error = rejectedFiles[0].errors[0];
      if (error.code === 'file-too-large') {
        toast.error('El archivo es demasiado grande. Máximo 10MB.');
      } else if (error.code === 'file-invalid-type') {
        toast.error('Tipo de archivo no válido. Solo se permiten PDF e imágenes.');
      } else {
        toast.error('Error al subir el archivo.');
      }
    },
  });

  const onSubmit = async (data) => {
    try {
      setLoading(true);

      // Create FormData for file upload
      const formData = new FormData();
      formData.append('nombre_solicitante', data.nombre_solicitante);
      formData.append('dni', data.dni);
      formData.append('area', data.area);
      if (data.descripcion) {
        formData.append('descripcion', data.descripcion);
      }
      if (data.numero_expediente) {
        formData.append('numero_expediente', data.numero_expediente);
      }
      if (uploadedFile) {
        formData.append('archivo_escaneado', uploadedFile);
      }

      const response = await expedienteService.createExpediente(formData);
      
      toast.success('¡Expediente creado exitosamente!');
      navigate(`/expedientes/${response.data.expediente.id}`);
    } catch (error) {
      toast.error('Error al crear el expediente');
    } finally {
      setLoading(false);
    }
  };

  const removeFile = () => {
    setUploadedFile(null);
    toast.info('Archivo removido');
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <Box>
      <Box sx={{ mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Nuevo Expediente
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Complete los datos del expediente. Los campos marcados con * son obligatorios.
        </Typography>
      </Box>

      {loading && <LinearProgress sx={{ mb: 2 }} />}

      <form onSubmit={handleSubmit(onSubmit)}>
        <Grid container spacing={3}>
          {/* Datos del Solicitante */}
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Datos del Solicitante
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Nombre del Solicitante *"
                    {...register('nombre_solicitante')}
                    error={!!errors.nombre_solicitante}
                    helperText={errors.nombre_solicitante?.message}
                    disabled={loading}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="DNI *"
                    {...register('dni')}
                    error={!!errors.dni}
                    helperText={errors.dni?.message}
                    disabled={loading}
                  />
                </Grid>
              </Grid>
            </Paper>
          </Grid>

          {/* Datos del Expediente */}
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Datos del Expediente
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Área *"
                    {...register('area')}
                    error={!!errors.area}
                    helperText={errors.area?.message}
                    disabled={loading}
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Número de Expediente"
                    {...register('numero_expediente')}
                    error={!!errors.numero_expediente}
                    helperText={errors.numero_expediente?.message || 'Si no se especifica, se generará automáticamente'}
                    disabled={loading}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    multiline
                    rows={4}
                    label="Descripción"
                    {...register('descripcion')}
                    error={!!errors.descripcion}
                    helperText={errors.descripcion?.message}
                    disabled={loading}
                  />
                </Grid>
              </Grid>
            </Paper>
          </Grid>

          {/* Archivo Escaneado */}
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Archivo Escaneado (Opcional)
              </Typography>
              
              {!uploadedFile ? (
                <Card
                  {...getRootProps()}
                  sx={{
                    border: 2,
                    borderStyle: 'dashed',
                    borderColor: isDragActive ? 'primary.main' : 'grey.300',
                    backgroundColor: isDragActive ? 'primary.light' : 'grey.50',
                    cursor: 'pointer',
                    p: 4,
                    textAlign: 'center',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      borderColor: 'primary.main',
                      backgroundColor: 'primary.light',
                    },
                  }}
                >
                  <input {...getInputProps()} />
                  <CloudUploadIcon
                    sx={{
                      fontSize: 48,
                      color: 'primary.main',
                      mb: 2,
                    }}
                  />
                  <Typography variant="h6" gutterBottom>
                    {isDragActive
                      ? 'Suelte el archivo aquí...'
                      : 'Arrastre un archivo aquí o haga clic para seleccionar'}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Se permiten archivos PDF e imágenes (PNG, JPG, JPEG, GIF)
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    Tamaño máximo: 10MB
                  </Typography>
                </Card>
              ) : (
                <Card sx={{ p: 2 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <DescriptionIcon color="primary" />
                    <Box sx={{ flexGrow: 1 }}>
                      <Typography variant="body1" fontWeight="bold">
                        {uploadedFile.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {formatFileSize(uploadedFile.size)}
                      </Typography>
                    </Box>
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={removeFile}
                      disabled={loading}
                    >
                      Remover
                    </Button>
                  </Box>
                </Card>
              )}
            </Paper>
          </Grid>

          {/* Actions */}
          <Grid item xs={12}>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/expedientes')}
                disabled={loading}
              >
                Cancelar
              </Button>
              <Button
                type="submit"
                variant="contained"
                startIcon={<SaveIcon />}
                disabled={loading}
              >
                {loading ? 'Creando...' : 'Crear Expediente'}
              </Button>
            </Box>
          </Grid>
        </Grid>
      </form>

      {/* Info Alert */}
      <Alert severity="info" sx={{ mt: 3 }}>
        <Typography variant="body2">
          <strong>Información:</strong> Al crear el expediente se generará automáticamente un 
          comprobante en PDF que podrá descargar desde la página de detalles del expediente.
        </Typography>
      </Alert>
    </Box>
  );
};

export default CreateExpedientePage;
